#include <iostream>
#include <vector>
#include <sstream>

template <typename T>
T getVar(const std::string& dialouge = "")
{
    T str{};
    std::cout << dialouge;
    std::cin >> str;

    return str;
}

std::vector<char> getLetters(const std::string& dialouge)
{
    std::vector<char> letters{};
    bool end{ false };
    while (!end)
    {
        char letter{ getVar<char>(dialouge) };
        if (letter != '.')
        {
            letters.push_back(letter);
        }
        else
        {
            end = true;
        }
    }

    return letters;
}

bool hasLetter(const std::string& word, char letter, bool type)
{
    for (char character : word)
    {
        if (character == letter)
        {
            return type;
        }
    }
    return !type;
}

bool hasLetterAndPos(const std::string& word, char letter, int pos)
{
    return (word[pos] == letter);
}

int main()
{

    static const std::string allWords{ """" };
    std::vector<char> nonLetters{ getLetters("enter a letter it doesn't have .-exit\n") };
    std::vector<char> noPosLetters{ getLetters("enter a letter in it .-exit\n") };
    std::vector<std::string> posLetters{};

    for (std::string inputString{}; inputString != ".";)
    {
        inputString = getVar<std::string>("enter a letter and position you know .-exit\n");
        if (inputString != "." && inputString.length() == 2) // add letter and number checking
        {
            posLetters.push_back(inputString);
        }
    }

    std::vector<std::string> realPossAnswers{};
    std::vector<std::string> possAnswers{};
    for (int i{}; i < allWords.length(); i += 5)
    {
        std::string sub{ allWords, static_cast<unsigned int>(i), 5 };
        possAnswers.push_back(sub);
    }
    for (std::string answer : possAnswers)
    {
        bool hasLetters{ true };
        bool doesntHaveLetters{ true };
        bool hasLettersInPos{ true };
        for (char letter : noPosLetters)
        {
            if (!hasLetter(answer, letter , true))
            {
                hasLetters = false;
                goto end;
            }
        }
        for (char letter : nonLetters)
        {
            if (!hasLetter(answer, letter, false))
            {
                doesntHaveLetters = false;
                goto end;
            }
        }
        for (std::string letterAndPos : posLetters)
        {
            if (!hasLetterAndPos(answer, letterAndPos[0], (static_cast<int>(letterAndPos[1]) - 49)))
            {
                hasLettersInPos = false;
                goto end;
            }
        }
    end:
        if (hasLetters && doesntHaveLetters && hasLettersInPos)
        {
            realPossAnswers.push_back(answer);
        }
    }

    std::cout << "\n real possiblities: \n";
    for (std::string word : realPossAnswers)
    {
        std::cout << word << '\t';
    }

    return 0;
}